import React, { useEffect, useRef } from 'react';
import * as THREE from 'three';
import '../styles/app.css';

const App = () => {
  const containerRef = useRef(null);
  let scene, camera, renderer;
  const flowers = [];
  const clouds = [];
  const textMeshes = [];
  const schools = [];
  const logos = [];

  use(() => {
    // Scene
    scene = new THREE.Scene();

    // Camera
    camera = new THREE.PerspectiveCamera(75, window.innerWidth / window.innerHeight, 0.1, 1000);
    camera.position.z = 5;

    // Renderer
    renderer = new THREE.WebGLRenderer();
    renderer.setSize(window.innerWidth, window.innerHeight);
    containerRef.current.appendChild(renderer.domElement);

    // Create a cube
    const geometry = new THREE.BoxGeometry();
    const material = new THREE.MeshBasicMaterial({ color: 0xffffff });
    const cube = new THREE.Mesh(geometry, material);
    scene.add(cube);

    // Create flowers
    function createFlower(x, y, z) {
      const flowerGeometry = new THREE.CylinderGeometry(0.1, 0.1, 1, 32);
      const flowerMaterial = new THREE.MeshBasicMaterial({ color: 0x8B4513 });
      const flowerStem = new THREE.Mesh(flowerGeometry, flowerMaterial);

      const flowerPetalGeometry = new THREE.SphereGeometry(0.3, 32, 32);
      const flowerPetalMaterial = new THREE.MeshBasicMaterial({ color: 0xFF69B4 });
      const flowerPetal = new THREE.Mesh(flowerPetalGeometry, flowerPetalMaterial);

      flowerStem.position.set(x, y, z);
      flowerPetal.position.set(x, y + 0.6, z);

      scene.add(flowerStem);
      scene.add(flowerPetal);

      flowers.push({ flowerStem, flowerPetal });
    }

    // Create clouds
    function createCloud(x, y, z) {
      const cloudGeometry = new THREE.SphereGeometry(1, 32, 32);
      const cloudMaterial = new THREE.MeshBasicMaterial({ color: 0xFFFFFF });
      const cloud = new THREE.Mesh(cloudGeometry, cloudMaterial);

      cloud.position.set(x, y, z);
      scene.add(cloud);

      clouds.push(cloud);
    }

    // Create text labels
    // function createText(x, y, z, text, size, color, url) {
    //   const loader = new THREE.FontLoader();
    //   loader.load('https://threejs.org/examples/fonts/helvetiker_regular.typeface.json', function (font) {
    //     const textGeometry = new THREE.TextGeometry(text, {
    //       font: font,
    //       size: size,
    //       height: 0.1,
    //       curveSegments: 2,
    //       bevelEnabled: false
    //     });
    //     const textMaterial = new THREE.MeshBasicMaterial({ color: color });
    //     const mesh = new THREE.Mesh(textGeometry, textMaterial);
    //     mesh.position.set(x, y, z);
    //     mesh.castShadow = true;
    //     mesh.userData.url = url;
    //     scene.add(mesh);
    //     textMeshes.push(mesh);
    //   });
    // }

    // Create school buildings
    function createSchool(x, y, z) {
      function createBuilding(xOffset, yOffset, zOffset) {
        const building = new THREE.Group();

        const wallGeometry = new THREE.BoxGeometry(5, 5, 2);
        const wallMaterial = new THREE.MeshBasicMaterial({ color: 0xffffff });
        const walls = new THREE.Mesh(wallGeometry, wallMaterial);
        walls.position.set(xOffset, yOffset, zOffset);

        const windowGeometry = new THREE.BoxGeometry(0.5, 0.7, 0.1);
        const windowMaterial = new THREE.MeshBasicMaterial({ color: 0x444444 });
        const windows = [
          { x: -1, y: 0.8, z: 1.01 },
          { x: 0, y: 0.8, z: 1.01 },
          { x: 1, y: 0.8, z: 1.01 },
          { x: -1, y: -1, z: 1.01 },
          { x: 0, y: -1, z: 1.01 },
          { x: 1, y: -1, z: 1.01 },
          { x: -2, y: 0.8, z: 1.01 },
          { x: -2, y: -1, z: 1.01 },
          { x: +2, y: 0.8, z: 1.01 },
          { x: +2, y: -1, z: 1.01 },
        ];

        windows.forEach((pos) => {
          const windowMesh = new THREE.Mesh(windowGeometry, windowMaterial);
          windowMesh.position.set(xOffset + pos.x, yOffset + pos.y, zOffset + pos.z);
          building.add(windowMesh);
        });

        const stripGeometry = new THREE.BoxGeometry(5, 0.3, 2);
        const stripMaterial = new THREE.MeshBasicMaterial({ color: 0x04192 });

        const strip1 = new THREE.Mesh(stripGeometry, stripMaterial);
        strip1.position.set(xOffset, yOffset + 1.5, zOffset);

        const strip2 = new THREE.Mesh(stripGeometry, stripMaterial);
        strip2.position.set(xOffset, yOffset - 0.2, zOffset);

        building.add(walls);
        building.add(strip1);
        building.add(strip2);

        return building;
      }

      const building1 = createBuilding(x, y, z);
      const building2 = createBuilding(x + 1, y, z * 2);

      scene.add(building1);
      scene.add(building2);

      schools.push(building1, building2);
    }

    // Create sun with rays
    function createSun(x, y, z) {
      const sunGroup = new THREE.Group();

      const sunGeometry = new THREE.CircleGeometry(1, 32, 0, Math.PI);
      const sunMaterial = new THREE.MeshBasicMaterial({ color: 0xFFFF00 });
      const sun = new THREE.Mesh(sunGeometry, sunMaterial);
      sun.position.set(x, y, z);
      sunGroup.add(sun);

      const rayMaterial = new THREE.LineBasicMaterial({ color: 0xd34000 });

      for (let i = 0; i <= Math.PI; i += Math.PI / 11) {
        const rayGeometry = new THREE.BufferGeometry();
        const vertices = new Float32Array([
          x + Math.cos(i) * 2, y + Math.sin(i) * 2, z,
          x + Math.cos(i) * 3, y + Math.sin(i) * 3, z
        ]);
        rayGeometry.setAttribute('position', new THREE.BufferAttribute(vertices, 3));
        const ray = new THREE.Line(rayGeometry, rayMaterial);
        sunGroup.add(ray);
      }

      scene.add(sunGroup);
      logos.push(sunGroup);
    }

    // Initial setup
    // createText(-5.5, 5, -5, "Welcome to O-rang", 1, 0xffffff);
    // createText(-4.3, 2, -5, "Hello I am Swati", 1, 0xffffff);
    // createText(-6.4, -3, -5, "A passionate developer and tech enthusiast", 0.5, 0xffffff);
    // createText(-3, -4.2, -5, "Let's move forward . .", 0.5, 0xffffff);

    createSchool(25, -3, -5);
    // createText(30, -3, -5, "Kendriya Vidyalaya Gorakhpur", 0.5, 0x7B0000, "/school");
    createSun(34.5, -2, -5);

    // Event listener for window resize
    window.addEventListener('resize', () => {
      camera.aspect = window.innerWidth / window.innerHeight;
      camera.updateProjectionMatrix();
      renderer.setSize(window.innerWidth, window.innerHeight);
    });

    // Animation function
    function animate() {
      requestAnimationFrame(animate);
      renderer.render(scene, camera);
    }

    animate();

    // Handle key press event
    document.addEventListener('keydown', (event) => {
      if (event.code === 'ArrowRight') {
        moveElements(-3);
      } else if (event.code === 'ArrowLeft') {
        moveElements(3);
      }
    });

    // Function to move elements
    function moveElements(xDistance) {
      // Move background elements
      flowers.forEach(flower => {
        flower.flowerStem.position.x -= xDistance;
        flower.flowerPetal.position.x -= xDistance;
      });
      clouds.forEach(cloud => {
        cloud.position.x -= xDistance;
      });
      textMeshes.forEach(textMesh => {
        textMesh.position.x -= xDistance;
      });
      schools.forEach(school => {
        school.position.x -= xDistance;
      });
      logos.forEach(logo => {
        logo.position.x -= xDistance;
      });

      // Change the shape of the cube
      cube.scale.x = 1 + Math.sin(xDistance) * 0.1;
      cube.scale.y = 1 + Math.cos(xDistance) * 0.1;
      cube.scale.z = 1 + Math.sin(xDistance) * 0.1;
    }

    // Cleanup
    return () => {
      // Clean up Three.js objects
      scene.remove(cube);
      flowers.forEach(flower => {
        scene.remove(flower.flowerStem);
        scene.remove(flower.flowerPetal);
      });
      clouds.forEach(cloud => {
        scene.remove(cloud);
      });
      textMeshes.forEach(textMesh => {
        scene.remove(textMesh);
      });
      schools.forEach(school => {
        scene.remove(school);
      });
      logos.forEach(logo => {
        scene.remove(logo);
      });

      // Dispose renderer
      renderer.dispose();
      containerRef.current.removeChild(renderer.domElement);
    };
  }, []); // Empty dependency array ensures useEffect runs only once

  return <div ref={containerRef} />;
};

export default App;
