import React, { useEffect, useRef } from 'react';
import * as THREE from 'three';
import { OrbitControls } from 'three/examples/jsm/controls/OrbitControls';

const ThreeScene = () => {
  const scene = useRef(null);
  const camera = useRef(null);
  const renderer = useRef(null);
  const cube = useRef(null);
  const controls = useRef(null);
  const notesDiv = useRef(null);

  const checkpoints = [0, 5, 8, 12, 30, 33];
  const notes = [
    "Checkpoint 1: Move the right arrow key!",
    "1", "2", "3",
    "Checkpoint 2: Done Schooling from kendriya vidyalaya Sangathan, with lots of happy memories and Freinds!",
    "Click to Enter in the school!"
  ];
  let checkpointIndex = 0;
  let xMovement = 0;
  const xdistance = 3;

  useEffect(() => {
    const initScene = () => {
      // Scene, camera, renderer setup
      scene.current = new THREE.Scene();
      camera.current = new THREE.PerspectiveCamera(75, window.innerWidth / window.innerHeight, 0.1, 1000);
      renderer.current = new THREE.WebGLRenderer();
      renderer.current.setSize(window.innerWidth, window.innerHeight);
      document.body.appendChild(renderer.current.domElement);

      // Cube setup
      const geometry = new THREE.BoxGeometry();
      const material = new THREE.MeshBasicMaterial({ color: 0xffffff });
      cube.current = new THREE.Mesh(geometry, material);
      scene.current.add(cube.current);

      // Orbit controls setup
      controls.current = new OrbitControls(camera.current, renderer.current.domElement);
      controls.current.enableDamping = true;
      controls.current.dampingFactor = 0.25;
      controls.current.screenSpacePanning = false;
      controls.current.maxPolarAngle = Math.PI / 2;

      // Set initial camera position
      camera.current.position.z = 5;

      // Create notes display
      notesDiv.current = document.createElement('div');
      notesDiv.current.style.position = 'absolute';
      notesDiv.current.style.textAlign = 'center';
      notesDiv.current.style.top = '30px';
      notesDiv.current.style.padding = '10px';
      notesDiv.current.style.border = '2px solid white';
      notesDiv.current.style.backgroundColor = 'black';
      notesDiv.current.style.zIndex = '1000';
      document.body.appendChild(notesDiv.current);

      // Event listeners
      document.addEventListener('mousedown', onDocumentMouseDown, false);
      document.addEventListener('keydown', onKeyDown);

      // Create elements
      createFlowers();
      createClouds();
      createText();
      createSchools();
      createSun();

      // Start animation loop
      animate();
    };

    const animate = () => {
      requestAnimationFrame(animate);

      // Update orbit controls
      controls.current.update();

      // Perform any other animations or updates
      cube.current.rotation.x += 0.01;
      cube.current.rotation.y += 0.01;

      // Render scene
      renderer.current.render(scene.current, camera.current);
    };

    const onKeyDown = (event) => {
      if (event.code === 'ArrowRight') {
        xMovement += 3;
        handleArrowRight();
      } else if (event.code === 'ArrowLeft') {
        xMovement -= 3;
        handleArrowLeft();
      }
    };

    const handleArrowRight = () => {
      cube.current.scale.x = 1 + Math.sin(xMovement) * 0.1;
      cube.current.scale.y = 1 + Math.cos(xMovement) * 0.1;
      cube.current.scale.z = 1 + Math.sin(xMovement) * 0.1;

      if (checkpointIndex < checkpoints.length && xMovement >= checkpoints[checkpointIndex]) {
        displayCheckpointNote();
        checkpointIndex++;
      }

      moveBackgroundElements(-xdistance);
    };

    const handleArrowLeft = () => {
      cube.current.scale.x = 1 + Math.sin(xMovement) * 0.1;
      cube.current.scale.y = 1 + Math.cos(xMovement) * 0.1;
      cube.current.scale.z = 1 + Math.sin(xMovement) * 0.1;

      if (checkpointIndex > 0 && xMovement < checkpoints[checkpointIndex - 1] && checkpointIndex > 4) {
        checkpointIndex--;
        displayCheckpointNote();
      }

      moveBackgroundElements(xdistance);
    };

    const moveBackgroundElements = (distance) => {
      // Replace with your actual logic for moving flowers, clouds, texts, schools, logos
      // Example:
      // flowers.forEach(flower => {
      //   flower.position.x += distance;
      // });
      // clouds.forEach(cloud => {
      //   cloud.position.x += distance;
      // });
      // textMeshes.forEach(textMesh => {
      //   textMesh.position.x += distance;
      // });
      // schools.forEach(school => {
      //   school.position.x += distance;
      // });
    };

    const displayCheckpointNote = () => {
      notesDiv.current.innerText = notes[checkpointIndex];
      notesDiv.current.style.display = 'block';
      setTimeout(() => {
        notesDiv.current.style.display = 'none';
      }, 2000);
    };

    const onDocumentMouseDown = (event) => {
      event.preventDefault();

      const raycaster = new THREE.Raycaster();
      const mouse = new THREE.Vector2();

      mouse.x = (event.clientX / window.innerWidth) * 2 - 1;
      mouse.y = -(event.clientY / window.innerHeight) * 2 + 1;

      raycaster.setFromCamera(mouse, camera.current);

      // Replace textMeshes with your actual objects
      // const intersects = raycaster.intersectObjects(textMeshes);
      // if (intersects.length > 0) {
      //   const intersectedObject = intersects[0].object;
      //   if (intersectedObject.userData.url) {
      //     window.open(intersectedObject.userData.url, '_blank');
      //   }
      // }
    };

    // Initialize scene when component mounts
    initScene();

    // Clean up Three.js scene when component unmounts
    return () => {
      // Dispose Three.js objects, remove event listeners, etc.
      scene.current = null;
      camera.current = null;
      renderer.current = null;
      cube.current = null;
      controls.current.dispose();
      controls.current = null;
      document.removeEventListener('mousedown', onDocumentMouseDown);
      document.removeEventListener('keydown', onKeyDown);
    };
  }, []);

  useEffect(() => {
    const handleResize = () => {
      if (camera.current) {
        camera.current.aspect = window.innerWidth / window.innerHeight;
        camera.current.updateProjectionMatrix();
        renderer.current.setSize(window.innerWidth, window.innerHeight);
      }
    };

    window.addEventListener('resize', handleResize);

    return () => {
      window.removeEventListener('resize', handleResize);
    };
  }, []);

  // Function to create flowers
  const createFlowers = () => {
    // Example implementation
    // const flowers = [];
    // createFlower(x, y, z);
  };

  // Function to create clouds
  const createClouds = () => {
    const clouds = [];

    function createCloud(x, y, z) {
      const cloudGeometry = new THREE.SphereGeometry(1, 32, 32);
      const cloudMaterial = new THREE.MeshBasicMaterial({ color: 0xFFFFFF });
      const cloud = new THREE.Mesh(cloudGeometry, cloudMaterial);

      cloud.position.set(x, y, z);
      scene.current.add(cloud);

      clouds.push(cloud);
    }

    // Example positions
    // createCloud(-5, 2, -10);
    // createCloud(0, 3, -10);
    // createCloud(5, 2, -10);
  };

  // Function to create text labels
  const createText = () => {
    // const loader = new THREE.FontLoader();
    // loader.load('https://threejs.org/examples/fonts/helvetiker_regular.typeface.json', function (font) {
    //   const createTextMesh = (text, x, y, z, size, color) => {
    //     const textGeometry = new THREE.TextGeometry(text, {
    //       font: font,
    //       size: size,
    //       height: 0.1,
    //       curveSegments: 2,
    //       bevelEnabled: false
    //     });
    //     const textMaterial = new THREE.MeshBasicMaterial({ color: color });
    //     const textMesh = new THREE.Mesh(textGeometry, textMaterial);
    //     textMesh.position.set(x, y, z);
    //     scene.current.add(textMesh);
    //   };

    //   // Example text labels
    //   // createTextMesh('Welcome to Three.js', -6, 5, -5, 1, 0xffffff);
    //   // createTextMesh('Hello, I am Swati', -4.3, 2, -5, 1, 0xffffff);
    //   // createTextMesh('A passionate developer and tech enthusiast', -6.4, -3, -5, 0.5, 0xffffff);
    //   // createTextMesh('Let\'s move forward...', -3, -4.2, -5, 0.5, 0xffffff);
    // });
  };

  // Function to create schools/buildings
  const createSchools = () => {
    const schools = [];

    function createSchool(x, y, z) {
      // Function to create a building
      function createBuilding(xOffset, yOffset, zOffset) {
        const building = new THREE.Group();

        // Create the walls
        const wallGeometry = new THREE.BoxGeometry(5, 5, 2);
        const wallMaterial = new THREE.MeshBasicMaterial({ color: 0xffffff }); // White color
        const walls = new THREE.Mesh(wallGeometry, wallMaterial);
        walls.position.set(xOffset, yOffset, zOffset);

        // Create windows
        const windowGeometry = new THREE.BoxGeometry(0.5, 0.7, 0.1);
        const windowMaterial = new THREE.MeshBasicMaterial({ color: 0x444444 });
        const windows = [
          { x: -1, y: 0.8, z: 1.01 },
          { x: 0, y: 0.8, z: 1.01 },
          { x: 1, y: 0.8, z: 1.01 },
          { x: -1, y: -1, z: 1.01 },
          { x: 0, y: -1, z: 1.01 },
          { x: 1, y: -1, z: 1.01 },
          { x: -2, y: 0.8, z: 1.01 },
          { x: -2, y: -1, z: 1.01 },
          { x: 2, y: 0.8, z: 1.01 },
          { x: 2, y: -1, z: 1.01 },
        ];

        windows.forEach((pos) => {
          const windowMesh = new THREE.Mesh(windowGeometry, windowMaterial);
          windowMesh.position.set(xOffset + pos.x, yOffset + pos.y, zOffset + pos.z);
          building.add(windowMesh);
        });

        // Create horizontal strips
        const stripGeometry = new THREE.BoxGeometry(5, 0.3, 2);
        const stripMaterial = new THREE.MeshBasicMaterial({ color: 0x04192 });

        const strip1 = new THREE.Mesh(stripGeometry, stripMaterial);
        strip1.position.set(xOffset, yOffset + 1.5, zOffset);

        const strip2 = new THREE.Mesh(stripGeometry, stripMaterial);
        strip2.position.set(xOffset, yOffset - 0.2, zOffset);

        // Add components to the building group
        building.add(walls);
        building.add(strip1);
        building.add(strip2);

        return building;
      }

      // Create two buildings
      const building1 = createBuilding(x, y, z);
      const building2 = createBuilding(x + 10, y, z);

      // Add the buildings to the scene
      scene.current.add(building1);
      scene.current.add(building2);

      schools.push(building1, building2);
    }

    // Example position
    createSchool(25, -3, -5);
  };

  // Function to create the sun
  const createSun = () => {
    const sunGroup = new THREE.Group();

    // Create the sun (half circle)
    const sunGeometry = new THREE.CircleGeometry(1, 32, 0, Math.PI);
    const sunMaterial = new THREE.MeshBasicMaterial({ color: 0xFFFF00 }); // Yellow color
    const sun = new THREE.Mesh(sunGeometry, sunMaterial);
    sun.position.set(34.5, -2, -5);
    sunGroup.add(sun);

    // Create rays
    const rayMaterial = new THREE.LineBasicMaterial({ color: 0xd34000 }); // Yellow color
    for (let i = 0; i <= Math.PI; i += Math.PI / 11) {
      const rayGeometry = new THREE.BufferGeometry();
      const vertices = new Float32Array([
        Math.cos(i) * 2, Math.sin(i) * 2, -5,
        Math.cos(i) * 3, Math.sin(i) * 3, -5
      ]);
      rayGeometry.setAttribute('position', new THREE.BufferAttribute(vertices, 3));
      const ray = new THREE.Line(rayGeometry, rayMaterial);
      sunGroup.add(ray);
    }
    // Add the sunGroup to the scene
    scene.current.add(sunGroup);
  };

  return <></>;
};

